import React from "react";
import { Image } from "react-native";
import { Card } from "react-native-paper";

const DisplayQRCodes = ({ QRCodes }) => {
  console.log(QRCodes);
  return (
    <Card>
      <Card.Content>
        {QRCodes.map((image, index) => (
          <Image
            style={{ flex: 0.2, width: 300, height: 300, borderRadius: 20 }}
            source={{ uri: image }}
          />
        ))}
      </Card.Content>
    </Card>
  );
};

export default DisplayQRCodes;
