import React, { useState } from "react";
import AskQRCodeForm from "components/CreateQRCodeContainer/AskQRCodeForm";
import DisplayQRCodes from "components/CreateQRCodeContainer/DisplayQRCodes";
import { StyleSheet, View } from "react-native";
import Service from "services/Service";
import GeneratePDF from "./GeneratePDF";

const CreateQRCodeContainer = () => {
  const [images, setImages] = useState([]);
  const [quantity, setQuantity] = useState(0);
  const [loading, setLoading] = useState(false);
  const [hide, setHide] = useState(false);

  const handleFormSubmit = async (data) => {
    setLoading(true);

    try {
      const resp = await Service.askForQR(data);
      const image = resp.data.images;
      var images = [];
      for (var i = 0; i < image.length; i++) {
        images.push(
          "data:image/png;base64," + image[i].substring(2, image[i].length - 1)
        );
      }
      setImages(images);
      setHide(true);
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <View>
      <AskQRCodeForm
        setQRCodes={setQRCodes}
        handleFormSubmit={handleFormSubmit}
      />
      {!hide && (
        <PDFDownloadLink
          document={<PdfDocument data={movieDetails} />}
          fileName="SaveLives.pdf"
          style={{
            textDecoration: "none",
            padding: "10px",
            color: "#4a4a4a",
            backgroundColor: "#f2f2f2",
            border: "1px solid #4a4a4a",
          }}
        >
          {({ blob, url, loading, error }) =>
            loading ? "Loading document..." : "Download Pdf"
          }
        </PDFDownloadLink>
      )}
      {/*       <View style={styles.rightCol}>
        <DisplayQRCodes QRCodes={QRCodes} loading={loading} />
      </View> */}
    </View>
  );
};

export default CreateQRCodeContainer;
