import React from "react";
import { StyleSheet, View } from "react-native";
import { Appbar, Button, Card } from "react-native-paper";
import AuthService from "services/authService";
import { useHistory } from "react-router-dom";
import LogoutButton from "./LogoutButton";
import GenerateQRCodeForm from "./GenerateQRCodeForm";

const Home = () => {
  return (
    <>
      <Appbar style={styles.top}>
        <LogoutButton style={styles.logout} />
      </Appbar>
      <View style={styles.container}>
        <View style={styles.column}>
          <GenerateQRCodeForm />
        </View>
        <View style={styles.column}>
          <GenerateQRCodeForm />
        </View>
      </View>
      {/*       <Card>
        <Card.Content></Card.Content>
      </Card> */}
    </>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    width: "100%",
  },
  row: {
    display: "flex",
  },
  column: {
    flex: "50%",
  },
  top: {
    position: "absolute",
    left: 0,
    right: 0,
    top: 0,
    zIndex: 2,
  },
  cardContainer: {
    width: "500px",
    marginLeft: "auto",
    marginRight: "auto",
    paddingTop: "80px",
    paddingBottom: "80px",
    borderColor: "#D3D3D3",
    borderWidth: "1px",
    borderRadius: "10px",
  },
  title: {
    textAlign: "center",
  },
  logout: {
    position: "absolute",
    right: "40px",
  },
});

export default Home;
