import React, { useState } from "react";
import { StyleSheet, View, Image } from "react-native";
import LogoutButton from "./LogoutButton";
import CreateQRCodeContainer from "components/CreateQRCodeContainer/CreateQRCodeContainer";
import ListQRCodes from "components/ListQRCodes/ListQRCodes";
import Service from "services/Service";

const Home = () => {
  const [QRCodeList, setQRCodeList] = useState([]);
  const [showQRCodeList, setShowQRCodeList] = useState(false);
  const [count, setCount] = useState(0);

  const isEntreprise = JSON.parse(localStorage.getItem("user")).role == "E";

  const handleList = () => {
    setShowQRCodeList(false);
    setCount(count + 1);
    Service.listQR()
      .then((resp) => {
        var array = [];
        for (var i = 0; i < resp.data.data.length; i++) {
          const images = resp.data.data[i].image;
          const count = resp.data.data[i].count;
          array.push({
            image:
              "data:image/png;base64," + images.substring(2, images.length - 1),
            count: count,
          });
        }
        setQRCodeList(array);
        setShowQRCodeList(true);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <Image
        source={require("assets/blockcovid-logo.png")}
        style={styles.logo}
      />
      <LogoutButton />
      <CreateQRCodeContainer handleList={handleList} />
      {showQRCodeList && <ListQRCodes data={QRCodeList} />}
    </>
  );
};

const styles = StyleSheet.create({
  logo: {
    position: "fixed",
    top: "40px",
    left: "50px",
    zIndex: 2,
    padding: "5px",
    width: 250,
    height: 50,
  },
});

export default Home;
