import React, { useState } from "react";
import { Document, Page,Image, Text, View, StyleSheet } from "@react-pdf/renderer";
import ReactDOM from 'react-dom';
import test from '../assets/icon.png'
import { PDFViewer } from '@react-pdf/renderer';


const GeneratePDF =  ({quantity,image}) => {
 
    const [submitted,setSubmitted] = useState(false)
    
    var CurrentPage = 1;
    var CurrentPageString=`Page ${CurrentPage}/${quantity}`
    
    const [MyPages,setMyPages] = useState([])

    if(!submitted){
        var testPages = [];
        
        for(var i=0;i<quantity;i++){
            CurrentPageString=`Page ${CurrentPage}/${quantity}`
            testPages.push(
                <Page size="A4" style={styles.page}>
                    <View style={styles.section}>
                        <Text>Scan me to save lives!!</Text>
                        <Image style={styles.image} source={image} />
                        <Text>{CurrentPageString}</Text>
                    </View> 
                </Page>);
            
            CurrentPage++;
        }
        setMyPages(testPages);
        setSubmitted(true)
    }



    
    return(
        <PDFViewer><Document style={styles.QR}>{MyPages.map(page => page)}</Document></PDFViewer>
    );
   
};

const styles = StyleSheet.create({
    page: {
      flexDirection: "row",
      width:'100%',
      textAlign:"center"
    },
    image: {
      display: "block",
      marginLeft: "auto",
      marginRight: "auto",
      width:"50%",
      padding: 10,
      backgroundColor: 'red',
      objectFit: 'cover',
    },
    section: {
      width: '100%',
      height: '100%',
      flexGrow: 1
    },
    QR:{
        width: 80% !important,
        height: 100% !important
    },
    footer: {
        height: 100
    }
});

ReactDOM.render(<GeneratePDF />, document.getElementById('root'));
export default GeneratePDF;
