import React, { useState } from "react";
import { Button,Image,StyleSheet,View,Text } from 'react-native';
import Service from "../services/Service";
import CustomInput from "./CustomInput";
import { Formik, Field } from "formik";
import * as yup from "yup";
//import toPDF from "./ConvertB64toPDF.js";


const loginSchema = yup.object({
    quantity: yup.number().required("quantity is required"),
    quantity: yup.number().typeError("That doesn't look like a number")
  });


const Home = () => {

  const imageStringBase64 = "";

  const testB64String = "iVBORw0KGgoAAAANSUhEUgAAADoAAAA6AQAAAADJwHeFAAAAgElEQVR4nK3RwQaEMQwE4CHXn32VkmuZVw+9Ln2VkmvJsuXfdHvczekjTIYg1hh+BaQOMIPuGhuKdpUvyAHzHWcORAs2nH1cau/MwKwyIkPkoWSG09p7daOYRmFGxIVnZEBiGDPodV6RURSjcYfpOvFBXTVu0Kd6ZECaKDP+88EXF1826vZ/jOsAAAAASUVORK5CYII="
  const [submitted, setSubmitted] = useState(false);
  const [QRImage,setQRImage] = useState(<div></div>)
  const [QRButton,setQRButton] = useState(<div></div>)

  const handleSubmitPDF = () => {
    toPDF([testB64String])
      .then(pdf => {
      console.log("pdf ", pdf);
    });
  }



  return (
    <div>
     <Formik
      style={styles.container}
      initialValues={{ quantity: "" }}
      onSubmit={(data, actions) => {
        setQRImage(<p>waiting...</p>);
        console.log(data);
        Service.askForQR(data).then((resp) =>{
            const imageStringBase64 = 'data:image/png;base64,'+resp.data.image.substring(2)
            
            setSubmitted(true);
            console.log(imageStringBase64);
            setQRButton(<Button onPress={handleSubmitPDF} title="Generate PDF" color="#FFD700"/>)
            setQRImage(<Image style={{ flex: 0.2,width:500, height:500,borderRadius: 20}} source={{uri: imageStringBase64}}/>);
            
        }) 
      }}
      validationSchema={loginSchema}
    >
      {({ handleSubmit, isValid }) => (
        <View style={styles.loginContainer}>
           {/*<Field component={CustomInput} name="Age" placeholder="How many QR Codes do you need? (example: 8)"/> */}
          <h2>How many QR Codes do you need? (example: 8)</h2>
          <input style={{margin:20,padding:15}} type="number" min="1" max="30" name="quantity" placeholder="How many QR Codes do you need? (example: 8)" />
          
        {/*isValid?"true":"false"*/}
        <Button
            onPress={handleSubmit}
            title="Ask QR Code Now"
            color="#841584"
            accessibilityLabel="Generate A Usable QRCode"
            disabled= {!isValid}  
        />
        {QRButton}
        </View>
      )}
    </Formik>    
     

    <div>{QRImage}</div>

    </div>
  );
};

const styles = StyleSheet.create({
    container: {
      justifyContent: "center",
      alignItems: "center",
    },
    loginContainer: {
      width: "30%",
      alignItems: "center",
      backgroundColor: "white",
      padding: 10,
      elevation: 10,
      backgroundColor: "#e6e6e6",
      justifyContent: "center",
    },
  });
  

export default Home;
