import React, { useState } from "react";
import AskQRCodeForm from "components/CreateQRCode/AskQRCodeForm";
import DisplayQRCodes from "components/CreateQRCode/DisplayQRCodes";
import { StyleSheet, View } from "react-native";
import { Card } from "react-native-paper";

const CreateQRCode = () => {
  const [QRCodes, setQRCodes] = useState([]);
  const [loading, setLoading] = useState(false);
  return (
    <View style={styles.container}>
      <View style={styles.leftCol}>
        <AskQRCodeForm setQRCodes={setQRCodes} />
      </View>
      <View style={styles.rightCol}>
        <DisplayQRCodes QRCodes={QRCodes} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    alignItems: "flex-start",
    flex: 1,
  },
  leftCol: {
    marginTop: "100px",
    width: "50%",
  },
  rightCol: {
    marginTop: "100px",
    width: "50%",
  },
});

export default CreateQRCode;
