import React, { useState } from "react";
import DataService from "../services/Service";
import { Button,Image,StyleSheet,View,Text } from 'react-native';
import Service from "../services/Service";
import CustomInput from "./CustomInput";
import { Formik, Field } from "formik";
import * as yup from "yup";


const loginSchema = yup.object({
    quantity: yup.number()
  });


const Home = () => {
  const imageStringBase64 = "";
  const initialDemandState = {
    id: null,
    quantity: "1"
  };
  const [data, setData] = useState(initialDemandState);
  const [submitted, setSubmitted] = useState(false);
  const [QRImage,setQRImage] = useState(<div></div>)



  return (
    <div>
     <Formik
      style={styles.container}
      initialValues={{ quantity: ""}}
      onSubmit={(data, actions) => {
        setQRImage(<p>waiting...</p>);
        console.log(data);
        Service.askForQR(data).then((resp) =>{
            const imageStringBase64 = 'data:image/png;base64,'+resp.data.image
            setSubmitted(true);
            console.log(imageStringBase64);
            setQRImage(<Image style={{ flex: 0.2,width:500, height:500,borderRadius: 20}} source={{uri: "data:image/jpeg;base64,"}}/>);

        }) 
      }}
      validationSchema={loginSchema}
    >
      {({ handleSubmit, isValid }) => (
        <View style={styles.loginContainer}>
          <Field component={CustomInput} name="Age" placeholder="How many QR Codes do you need? (example: 8)"/>
          <input style={{margin:20,padding:15}} type="number" min="1" name="quantity" id="Quantity" placeholder="How many QR Codes do you need? (example: 8)" />
          
          <div>{isValid}</div>
        <Button
            onPress={handleSubmit}
            title="Ask QR Code Now"
            color="#841584"
            accessibilityLabel="Generate A Usable QRCode"
            disabled={!isValid}
            
        />
        </View>
      )}
    </Formik>    
     

    <div>{QRImage}</div>

    </div>
  );
};

const styles = StyleSheet.create({
    container: {
      justifyContent: "center",
      alignItems: "center",
    },
    loginContainer: {
      width: "30%",
      alignItems: "center",
      backgroundColor: "white",
      padding: 10,
      elevation: 10,
      backgroundColor: "#e6e6e6",
      justifyContent: "center",
    },
  });
  

export default Home;
