import React from "react";
import {
  Page,
  Text,
  View,
  Document,
  StyleSheet,
  Image,
} from "@react-pdf/renderer";
import ReactDOM from "react-dom";

const PDFDocument = ({ data }) => {
  console.log(data.length);
  return (
    <Document>
      {data
        ? data.map((image, index) => {
            <Page style={styles.page} key={index} size="A4">
              <View style={styles.section}>
                <Text>Scan me to save lives!!</Text>
                <Image style={styles.image} source={image} />
                <Text>{`Page ${index + 1}/${data.length}`}</Text>
              </View>
            </Page>;
          })
        : ""}
      <Page style={styles.page}></Page>
    </Document>
  );
};

const styles = StyleSheet.create({
  page: {
    flexDirection: "row",
    width: "100%",
    textAlign: "center",
  },
  image: {
    display: "block",
    marginLeft: "auto",
    marginRight: "auto",
    width: "50%",
    padding: 10,
    backgroundColor: "red",
    objectFit: "cover",
  },
  section: {
    width: "100%",
    height: "100%",
    flexGrow: 1,
  },
  QR: {
    height: 500,
    scale: 0.5,
  },
  footer: {
    height: 100,
  },
});

ReactDOM.render(<PDFDocument />, document.getElementById("root"));
export default PDFDocument;
