import React, { useState } from "react";
import AskQRCodeForm from "components/CreateQRCode/AskQRCodeForm";
import DisplayQRCodes from "components/CreateQRCode/DisplayQRCodes";
import { StyleSheet, View } from "react-native";
import Service from "services/Service";

const CreateQRCodeContainer = () => {
  const [QRCodes, setQRCodes] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFormSubmit = (data) => {
    setLoading(true);
    Service.askForQR(data).then((resp) => {
      const image = resp.data.images;
      var images = [];
      for (var i = 0; i < image.length; i++) {
        images.push(
          "data:image/png;base64," + image[i].substring(2, image[i].length - 1)
        );
      }
      setQRCodes(images);
      setLoading(false);
    });
  };

  return (
    <View style={styles.container}>
      <View style={styles.leftCol}>
        <AskQRCodeForm
          setQRCodes={setQRCodes}
          handleFormSubmit={handleFormSubmit}
        />
      </View>
      <View style={styles.rightCol}>
        <DisplayQRCodes QRCodes={QRCodes} loading={loading} />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    alignItems: "flex-start",
    flex: 1,
  },
  leftCol: {
    marginTop: "100px",
    width: "50%",
  },
  rightCol: {
    marginTop: "100px",
    width: "50%",
  },
});

export default CreateQRCodeContainer;
