import React, { useState } from "react";
import { View, StyleSheet } from "react-native";
import { RadioButton, Card, Title, Text } from "react-native-paper";

const SelectAccountForm = () => {
  const [account, setAccount] = useState("");
  return (
    <Card.Content>
      <Title style={styles.title}>Selectionner votre type de compte</Title>
      <RadioButton.Group
        onValueChange={(newValue) => setAccount(newValue)}
        value={account}
      >
        <RadioButton.Item
          style={styles.radioButton}
          color="#03dac6"
          label="Médecin"
          value="Médecin"
        />
        <RadioButton.Item
          style={styles.radioButton}
          color="#03dac6"
          label="Établissement"
          value="Établissement"
        />
      </RadioButton.Group>
    </Card.Content>
  );
};

const styles = StyleSheet.create({
  radioButton: {
    marginTop: "50px",
  },
  title: {
    textAlign: "center",
  },
  buttons: {
    justifyContent: "space-between",
  },
});

export default SelectAccountForm;
