import React, { useState } from "react";
import { View, StyleSheet } from "react-native";
import {
  RadioButton,
  Card,
  Title,
  Text,
  Button,
  IconButton,
} from "react-native-paper";
import backIcon from "assets/svg/arrow-left.svg";

const SelectAccountForm = ({ setShowRegisterForm, setAccount }) => {
  const [value, setValue] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = () => {
    if (value === "") {
      setError("Veuillez sélectionner une option");
    }
    setAccount(value);
  };
  return (
    <Card.Content>
      <Title style={styles.title}>Selectionner votre type de compte</Title>
      <View style={styles.radioButtons}>
        <RadioButton.Group
          onValueChange={(newValue) => setValue(newValue)}
          value={value}
        >
          <RadioButton.Item color="#03dac6" label="Médecin" value="Médecin" />
          <RadioButton.Item
            color="#03dac6"
            label="Établissement"
            value="Établissement"
          />
        </RadioButton.Group>
        <Text style={styles.textError}>{error}</Text>
      </View>
      <Card.Actions style={styles.buttons}>
        <IconButton
          color="#808080"
          icon={backIcon}
          onPress={() => setShowRegisterForm(false)}
        />
        <Button mode="contained" onPress={handleSubmit}>
          Suivant
        </Button>
      </Card.Actions>
    </Card.Content>
  );
};

const styles = StyleSheet.create({
  radioButtons: {
    marginBottom: "25px",
    marginTop: "50px",
    height: "75px",
  },
  title: {
    textAlign: "center",
  },
  textError: {
    position: "absolute",
    color: "red",
    textAlign: "center",
  },
  buttons: {
    justifyContent: "space-between",
  },
});

export default SelectAccountForm;
