import React, { useState } from "react";
import { View, StyleSheet } from "react-native";
import {
  RadioButton,
  Card,
  Title,
  Text,
  Button,
  IconButton,
} from "react-native-paper";
import backIcon from "assets/svg/arrow-left.svg";

const SelectAccountForm = () => {
  const [account, setAccount] = useState("");
  const [showDoctorForm, setShowDoctorForm] = useState(false);

  const showRegisterForm = () => {};
  return (
    <Card.Content>
      <Title style={styles.title}>Selectionner votre type de compte</Title>
      <View style={styles.radioButtons}>
        <RadioButton.Group
          onValueChange={(newValue) => setAccount(newValue)}
          value={account}
        >
          <RadioButton.Item color="#03dac6" label="Médecin" value="Médecin" />
          <RadioButton.Item
            color="#03dac6"
            label="Établissement"
            value="Établissement"
          />
        </RadioButton.Group>
      </View>
      {/*       <Card.Actions style={styles.buttons}>
        <IconButton
          color="#808080"
          icon={backIcon}
          onPress={() => setShowSelectAccountForm(false)}
        />
        <Button mode="contained" onPress={showRegisterForm}>
          Suivant
        </Button>
      </Card.Actions> */}
    </Card.Content>
  );
};

const styles = StyleSheet.create({
  radioButtons: {
    marginBottom: "25px",
    marginTop: "50px",
  },
  title: {
    textAlign: "center",
  },
  buttons: {
    justifyContent: "space-between",
  },
});

export default SelectAccountForm;
