import React, { useState, useEffect } from "react";
import { StyleSheet, View, Image,Text } from "react-native";
import Service from "services/Service";
import { Card, Button,DataTable } from "react-native-paper";

const ListQRCodes = ({datas}) => {
    
    const [codes, setCodes] = useState([]);
    const [currentPage,setCurrentPage] = useState(0);
    const [load,setLoad]=useState(false);

    const itemsPerPage = 1;
    console.log(datas)
    if(!load){
        const array= []
        console.log(datas)
        for(var i=0;i<datas.length;i++){
            const temp = (
                <DataTable.Row key={i}>
                    <DataTable.Cell><Image style={styles.image} source={datas[i].image} /></DataTable.Cell>
                    <DataTable.Cell numeric>{datas[i].count}</DataTable.Cell>
                </DataTable.Row>)
                array.push(temp)
                console.log(i)
        }
        setLoad(true);
        setCodes(array);
    } 
    return (
        <Card.Content>
          <DataTable>
            <DataTable.Header>
              <DataTable.Title>QR Code</DataTable.Title>
              <DataTable.Title numeric>nbr of scans</DataTable.Title>
            </DataTable.Header>

          {codes[currentPage]}     

          <DataTable.Pagination
              page={currentPage}
              numberOfPages={codes.length}
              perPage={itemsPerPage}
              onPageChange={page => {
                setCurrentPage(page)
              }}
              label={`${currentPage+1} of ${codes.length}`}
            />
          </DataTable>         
        </Card.Content>   
    );
  };
const styles = StyleSheet.create({
    cardContainer: {
      marginLeft: "auto",
      marginRight: "auto",
      paddingTop: "80px",
      paddingBottom: "80px",
      borderColor: "#D3D3D3",
      borderWidth: "1px",
      borderRadius: "10px",
    },
    downloadButton: {},
    image: {
        display: "block",
        height:"100",
        width: "100",
        marginLeft: "auto",
        marginRight: "auto",
        padding: 40,
        backgroundColor: "red",
        objectFit: "cover",
      }
  });
  
  export default ListQRCodes;
