import React, { useState } from "react";
import AskQRCodeForm from "components/CreateQRCodeContainer/AskQRCodeForm";
import DisplayQRCodes from "components/CreateQRCodeContainer/DisplayQRCodes";
import { StyleSheet, View } from "react-native";
import Service from "services/Service";
import GeneratePDF from "./GeneratePDF";

const CreateQRCodeContainer = () => {
  const [QRCodes, setQRCodes] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleFormSubmit = (data) => {
    setLoading(true);
    Service.askForQR(data).then((resp) => {
      const image = resp.data.images;
      var images = [];
      for (var i = 0; i < image.length; i++) {
        images.push(
          "data:image/png;base64," + image[i].substring(2, image[i].length - 1)
        );
      }
      setQRCodes(images);
      setLoading(false);
    });
  };
  console.log(QRCodes.length);
  return (
    <View>
      <AskQRCodeForm
        setQRCodes={setQRCodes}
        handleFormSubmit={handleFormSubmit}
      />
      <GeneratePDF images={QRCodes} quantity={QRCodes.length} />

      {/*       <View style={styles.rightCol}>
        <DisplayQRCodes QRCodes={QRCodes} loading={loading} />
      </View> */}
    </View>
  );
};

export default CreateQRCodeContainer;
