import React, { useState } from "react";
import { StyleSheet, View } from "react-native";
import LogoutButton from "./LogoutButton";
import CreateQRCodeContainer from "components/CreateQRCodeContainer/CreateQRCodeContainer";
import ListQRCodes from "components/ListQRCodes/ListQRCodes";
import Service from "services/Service";



const Home = () => {

  const [display,setDisplay] = useState(false)
  const [data,setData] = useState([])
  const isEntreprise = JSON.parse(localStorage.getItem("user")).role=="E";

  const handleList = () =>{
    if(display)return data
    setDisplay(true)

    Service.listQR()
      .then((resp) => {
        var array = []
        var images = [];
        var count = [];

        for (var i = 0; i < resp.data.data.length; i++) {
          images = resp.data.data[i].images;
          count = resp.data.data[i].count;
          array.push({
            image:"data:image/png;base64," + images.substring(2, images.length - 1), count:count}
          );
        }
        
        setData(array)

        console.log("ff",data.length)
        return (data)
      })
      .catch((error) => {
        console.log(error);
      });
      
      
  }
  
 
 
    
  
  
  
  const data2 = [{image:"data:image/png;base64,",count:5 },
    {image:"data:image/png;base64,",count:7 }]
  return (
    <>
      <LogoutButton />
      <CreateQRCodeContainer />
      {!display?handleList():isEntreprise ? <ListQRCodes data={handleList()}/>:"ok"}
      
    </>
  );
};

export default Home;
