import React, { useState } from "react";
import { Switch, Route } from "react-router-dom";
import { StyleSheet, View } from "react-native";
// import { Route, Switch, Link } from 'react-router-native';
import RegisterForm from "components/AuthForm/RegisterForm";
import LoginForm from "components/AuthForm/LoginForm";
import Home from "components/Home";
import { Avatar, Button, Card, Title, Paragraph } from "react-native-paper";

const App = () => {
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  return (
    <Switch>
      <View style={styles.container}>
        {showRegisterForm ? (
          <Route exact path="/register" exact>
            <RegisterForm setShowRegisterForm={setShowRegisterForm} />
          </Route>
        ) : (
          <LoginForm setShowRegisterForm={setShowRegisterForm} />
        )}
      </View>
    </Switch>
    /*<Switch>
       <Route exact path="/add" exact>
         <AjouterTuto />
       </Route>
       <Route exact path="/register" exact>
         <RegisterForm />
       </Route>
       <Route exact path="/login" exact>
         <LoginForm />
       </Route>
       <Route exact path="/" exact>
         <Home />
       </Route>
      </Switch>*/
  );
};

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    alignItems: "center",
    flex: 1,
  },
});

export default App;
