import React, { useState } from "react";
import DataService from "../services/Service";
import { Button,Image,StyleSheet,View,Text } from 'react-native';


const Home = () => {
  const imageStringBase64 = "";
  const initialDemandState = {
    id: null,
    quantity: "1"
  };
  const [data, setData] = useState(initialDemandState);
  const [submitted, setSubmitted] = useState(false);
  const [QRImage,setQRImage] = useState(<div></div>)

  var container = styles.container

  const saveData = () => {
    setQRImage(<p>waiting...</p>);
    var data = {
      quantity: "1"
    };

    DataService.askForQR(data)
      .then(response => {
        setData({
          id: response.data.id,
          image: response.data.image
        });
        const imageStringBase64 = 'data:image/png;base64,'+response.data.image
        setSubmitted(true);
        console.log(response.data);
        console.log(imageStringBase64);
        
        setQRImage(<Image style={{ flex: 0.2,width:500, height:500,borderRadius: 20}} source={{uri: "data:image/jpeg;base64,"}}/>);
      })
      .catch(e => {
        console.log(e);
      });
  };

  

  return (
    <div>
      <Button
        onPress={saveData}
        title="Ask QR Code Now"
        color="#841584"
        accessibilityLabel="Generate A Usable QRCode"
      />

        <div style={{container}}>{QRImage}</div>
        


      
      

    </div>
  );
};

const styles = StyleSheet.create({
    container: {
      display: "flex",
      flexDirection: "vertical",
      justifyContent: "space-around",
      alignItems: "center",
      height: "100%",
      textAlign: "center"
    }
});
  

export default Home;
