import React, { useState } from "react";
import { Image, StyleSheet, View } from "react-native";
import CustomInput from "components/utils/CustomInput";
import { Formik, Field } from "formik";
import * as yup from "yup";
import GeneratePDF from "./GeneratePDF";
import { Card, Title, Button } from "react-native-paper";

const loginSchema = yup.object({
  quantity: yup.number().required("quantity is required"),
  quantity: yup.number().typeError("That doesn't look like a number"),
});

const AskQRCodeForm = ({ handleFormSubmit }) => {
  const [PDF, setPDF] = useState(<div></div>);

  const handleSubmitPDF = (testB64String, quantity) => {
    setPDF(<GeneratePDF quantity={quantity} image={testB64String} />);
  };

  return (
    <>
      <Formik
        initialValues={{ quantity: 1 }}
        onSubmit={(data) => handleFormSubmit(data)}
        validationSchema={loginSchema}
      >
        {({ handleSubmit, isValid }) => (
          <Card style={styles.cardContainer}>
            <Card.Content>
              <Title style={styles.title}>
                Combien de QR Code avez-vous besoin ?
              </Title>
              <Field
                component={CustomInput}
                name="quantity"
                placeholder="1"
                type="number"
                min="1"
                max="30"
              />
              <Card.Actions style={styles.button}>
                <Button mode="contained" onPress={handleSubmit}>
                  Ask QR Code Now
                </Button>
              </Card.Actions>
            </Card.Content>
          </Card>
        )}
      </Formik>
      {/*       <div>{QRImage}</div>

      <View>{PDF}</View> */}
    </>
  );
};

const styles = StyleSheet.create({
  cardContainer: {
    marginLeft: "auto",
    marginRight: "auto",
    paddingTop: "80px",
    paddingBottom: "80px",
    borderColor: "#D3D3D3",
    borderWidth: "1px",
    borderRadius: "10px",
  },
  numberInput: {
    textAlign: "center",
  },
  title: {
    textAlign: "center",
  },
  button: {
    justifyContent: "center",
    textAlign: "center",
  },
  QR: {
    height: 100,
    scale: 0.5,
  },
});

export default AskQRCodeForm;
