import React, { useState } from "react";
import DataService from "../services/Service";
import { Button,Image } from 'react-native';


const Home = () => {
  const imageStringBase64 = "";
  const initialDemandState = {
    id: null,
    quantity: "1"
  };
  const [data, setData] = useState(initialDemandState);
  const [submitted, setSubmitted] = useState(false);

  var QRImage = <div></div>

  const saveData = () => {
    var data = {
      quantity: "2"
    };
    QRImage = <p> waiting...</p>
    DataService.askForQR(data)
      .then(response => {
        setData({
          id: response.data.id,
          title: response.data.title,
          description: response.data.description,
          published: response.data.published
        });
        const imageStringBase64 = 'data:image/png;base64,'+response.data.image
        setSubmitted(true);
        console.log(response.data);
        console.log(imageStringBase64);
        QRImage =  () =><Image style={{ flex: 0.2,width:200, height:200, resizeMode: 'contain' }} source={{uri: imageStringBase64}}/>
      })
      .catch(e => {
        console.log(e);
      });
  };

  

  return (
    <div >
      <Button
        onPress={saveData}
        title="Ask QR Code Now"
        color="#841584"
        accessibilityLabel="Learn more about this purple button"
      />
      {QRImage}
      

    </div>
  );
};

export default Home;
