import React, { useState } from "react";
import { Document, Page,Image, Text, View, StyleSheet } from "@react-pdf/renderer";
import ReactDOM from 'react-dom';
import test from '../assets/icon.png'
import { PDFViewer } from '@react-pdf/renderer';


const GeneratePDF =  ({quantity,image}) => {

    quantity=4;

    const [submitted,setSubmitted] = useState(false)
    const uriPDF = "data:image/png;base64,"
      

    var CurrentPage = 1;
    var CurrentPageString=`Page ${CurrentPage}/${quantity}`
    
    const [MyPages,setMyPages] = useState([])



    if(!submitted){
        const newPages = ( 
            <Page size="A4" style={styles.page}>
                <View style={styles.section}>
                    <Text>Scan me to save lives!!</Text>
                    <Image style={styles.image} source={uriPDF} alt="images" />
                    <Text>{CurrentPageString}</Text>
                </View> 
            </Page>);
        
        for(var i=0;i<quantity;i++){
            console.log(i<quantity)
          
            console.log(newPage)
            setMyPages([...MyPages, newPage]);
            CurrentPage++;
            console.log(MyPages)
        }
        
        setSubmitted(true)
    }

   
    const MyDocument = (
        <Document>
          {MyPages.map(page => page)}
        </Document>
    );

    
    return(
        <PDFViewer>{MyDocument}</PDFViewer>
    );
   
};

const styles = StyleSheet.create({
    page: {
      flexDirection: "row",
      width:'100%',
      textAlign:"center"
    },
    image: {
      display: "block",
      marginLeft: "auto",
      marginRight: "auto",
      width:"50%",
      padding: 10,
      backgroundColor: 'red',
      objectFit: 'cover',
    },
    section: {
      width: '100%',
      height: '100%',
      flexGrow: 1
    },
    footer: {
        height: 100
    }
});

ReactDOM.render(<GeneratePDF />, document.getElementById('root'));
export default GeneratePDF;
