import React from "react";
import { StyleSheet } from "react-native";
import { Formik, Field } from "formik";
import * as yup from "yup";
import AuthService from "services/authService";
import CustomInput from "components/AuthForm/CustomInput";
import { Button, Card, Title } from "react-native-paper";
import mailIcon from "assets/svg/mail.svg";
import passwordIcon from "assets/svg/lock.svg";

const loginSchema = yup.object({
  email: yup.string().required("Email is required"),
  password: yup.string().required("Password is required"),
});

const LoginForm = ({ setShowRegisterForm }) => {
  return (
    <Formik
      initialValues={{ email: "", password: "" }}
      onSubmit={(data, actions) => {
        console.log(data);
        AuthService.authenticateUser(data).then((resp) => console.log(resp));
      }}
      validationSchema={loginSchema}
    >
      {({ handleSubmit, isValid }) => (
        <Card.Content>
          {/*          <TextInput
            mode="outlined"
            style={styles.textInput}
            theme={{
              colors: { primary: "green", underlineColor: "transparent" },
            }}
          /> */}
          <Title style={styles.title}>Connectez vous</Title>
          <Field
            icon={mailIcon}
            component={CustomInput}
            name="Email"
            label="Email"
          />
          <Field
            icon={passwordIcon}
            component={CustomInput}
            type="password"
            name="Password"
            label="Password"
            secureTextEntry
          />
          <Card.Actions style={styles.buttons}>
            <Button mode="flat" onPress={() => setShowRegisterForm(true)}>
              Cr√©er un compte
            </Button>
            <Button mode="contained" onPress={handleSubmit}>
              Se connecter
            </Button>
          </Card.Actions>
        </Card.Content>
      )}
    </Formik>
  );
};

const styles = StyleSheet.create({
  textInput: {
    height: 40,
    margin: 10,
    backgroundColor: "white",
  },
  title: {
    textAlign: "center",
  },
  buttons: {
    justifyContent: "space-between",
  },
});

export default LoginForm;
