import React, { useState, useEffect } from "react";
import { StyleSheet, View, Image,Text } from "react-native";
import Service from "services/Service";
import { Card, Button,DataTable } from "react-native-paper";

const ListQRCodes = ({data}) => {
    const [codes, setCodes] = useState([]);
    
    return (
        <Card.Content>
          <DataTable>
            <DataTable.Header>
              <DataTable.Title>QR Code</DataTable.Title>
              <DataTable.Title numeric>nbr of scans</DataTable.Title>
            </DataTable.Header>

          {data.map((code,index) => {
            codes.push(<DataTable.Row>
                <DataTable.Cell>{`${index}.${code.image}`}</DataTable.Cell>
                <DataTable.Cell numeric>${code.count}</DataTable.Cell>
              </DataTable.Row>);
            setCodes(codes)
          })}

          <DataTable.Pagination
              page={1}
              numberOfPages={1}
              onPageChange={page => {
                console.log(page);
              }}
              label="1-2 of 2"
            />
          </DataTable>
          
        </Card.Content>      
    );
  };



const styles = StyleSheet.create({
    cardContainer: {
      marginLeft: "auto",
      marginRight: "auto",
      paddingTop: "80px",
      paddingBottom: "80px",
      borderColor: "#D3D3D3",
      borderWidth: "1px",
      borderRadius: "10px",
    },
    downloadButton: {},
  });
  
  export default ListQRCodes;
