import React, { useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";
import { StyleSheet, View } from "react-native";
// import { Route, Switch, Link } from 'react-router-native';
import RegisterForm from "components/AuthForm/RegisterForm";
import LoginForm from "components/AuthForm/LoginForm";
/* import Home from "components/Home";*/
import Home from "components/Home/Home";
import AuthService from "services/authService";
import PrivateRoute from "components/utils/PrivateRoute";

const App = () => {
  const [showRegisterForm, setShowRegisterForm] = useState(false);

  return (
    <Switch>
      <Route exact path="/" exact>
        <View style={styles.container}>
          {showRegisterForm ? (
            <RegisterForm setShowRegisterForm={setShowRegisterForm} />
          ) : (
            <LoginForm setShowRegisterForm={setShowRegisterForm} />
          )}
        </View>
      </Route>
      {/**Private Route redirige directement vers la page de login si un utilisateur non connecté essaye d'acceder à la ressource */}

      <PrivateRoute path="/home" component={Home} />
    </Switch>
  );
};

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    alignItems: "center",
    flex: 1,
  },
});

export default App;
