import React from "react";

import { Document, Page,Image, Text, View, StyleSheet } from "@react-pdf/renderer";
import ReactDOM from 'react-dom';
import test from '../assets/icon.png'


import { PDFViewer } from '@react-pdf/renderer';

const styles = StyleSheet.create({
  page: {
    flexDirection: "row",
    width:'100%',
    textAlign:"center"
  },
  image: {
    display: "block",
    marginLeft: "auto",
    marginRight: "auto",
    width:"50%",
    padding: 10,
    backgroundColor: 'red',
    objectFit: 'cover',
  },
  section: {
    width: '100%',
    height: '100%',
    flexGrow: 1
  }
});
const uriPDF = "data:image/png;base64,"
var totalPages = 1
var MyPages=(
<Page size="A4" style={styles.page}>
    <View style={styles.section}>
        <Text>Scan me to save lives!!</Text>
        <Image style={styles.image} source={uriPDF} alt="images" />
    </View>
</Page>
)
const addPage = () =>{
    MyPages=MyPages+(<Page size="A4" style={styles.page}><View style={styles.section}><Text>Scan me to save lives!!</Text><Image style={styles.image} source={uriPDF} alt="images" /></View> </Page>)
    totalPages++
} 

const MyDocument = (
  <Document>
    {MyPages}
  </Document>
);


const PDF =  () => <PDFViewer>{MyDocument}</PDFViewer>;

ReactDOM.render(<PDF />, document.getElementById('root'));

export default PDF;
