import React, { useState } from "react";
import { View, StyleSheet } from "react-native";
import { RadioButton, Card, Title, Text } from "react-native-paper";

const SelectAccountForm = () => {
  const [account, setAccount] = useState("");
  return (
    <Card.Content>
      <Title style={styles.title}>Selectionner votre type de compte</Title>
      <RadioButton.Group
        onValueChange={(newValue) => setAccount(newValue)}
        value={account}
      >
        <RadioButton.Item color="#03dac6" label="Médecin" value="Médecin" />
        <RadioButton.Item
          color="#03dac6"
          label="Établissement"
          value="Établissement"
        />
      </RadioButton.Group>
      <Card.Actions style={styles.buttons}>
        <Button mode="flat" onPress={() => setShowRegisterForm(true)}>
          Précédent
        </Button>
        <Button mode="contained" onPress={handleSubmit}>
          Suivant
        </Button>
      </Card.Actions>
    </Card.Content>
  );
};

const styles = StyleSheet.create({
  title: {
    textAlign: "center",
    marginBottom: "50px",
  },
  buttons: {
    justifyContent: "space-between",
  },
});

export default SelectAccountForm;
