import React, { useState } from "react";
import { Switch, Route } from "react-router-dom";
import { StyleSheet, View } from "react-native";
// import { Route, Switch, Link } from 'react-router-native';
import SelectAccountForm from "components/AuthForm/SelectAccountForm";
import LoginForm from "components/AuthForm/LoginForm";
import Home from "components/Home";
import { Avatar, Button, Card, Title, Paragraph } from "react-native-paper";

const App = () => {
  const [showSelectAccountForm, setShowSelectAccountForm] = useState(false);
  return (
    <View style={styles.container}>
      <Card style={styles.loginContainer}>
        {showSelectAccountForm ? (
          <SelectAccountForm
            setShowSelectAccountForm={setShowSelectAccountForm}
          />
        ) : (
          <LoginForm setShowSelectAccountForm={setShowSelectAccountForm} />
        )}
      </Card>
    </View>
    /*<Switch>
       <Route exact path="/add" exact>
         <AjouterTuto />
       </Route>
       <Route exact path="/register" exact>
         <RegisterForm />
       </Route>
       <Route exact path="/login" exact>
         <LoginForm />
       </Route>
       <Route exact path="/" exact>
         <Home />
       </Route>
      </Switch>*/
  );
};

const styles = StyleSheet.create({
  container: {
    justifyContent: "center",
    alignItems: "center",
    flex: 1,
  },
  loginContainer: {
    width: "500px",
    marginLeft: "auto",
    marginRight: "auto",
    paddingTop: "80px",
    paddingBottom: "80px",
    borderColor: "#D3D3D3",
    borderWidth: "1px",
    borderRadius: "10px",
  },
});

export default App;
